import React, { useState, useEffect } from 'react';
import MocionesList from './components/MocionesList';
import MocionForm from './components/MocionForm';

const App = () => {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/mociones')
            .then((response) => response.json())
            .then((data) => setMociones(data));
    }, []);

    const addMocion = (newMocion) => {
        fetch('http://localhost:8080/mociones', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newMocion),
        })
            .then((response) => response.json())
            .then((mocion) => setMociones([...mociones, mocion]));
    };

    const updateMocion = (updatedMocion) => {
        fetch(`http://localhost:8080/mociones/${updatedMocion.ID}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedMocion),
        })
            .then((response) => response.json())
            .then((mocion) => {
                setMociones(
                    mociones.map((m) => (m.ID === mocion.ID ? mocion : m))
                );
            });
    };

    const deleteMocion = (id) => {
        fetch(`http://localhost:8080/mociones/${id}`, {
            method: 'DELETE',
        }).then(() => {
            setMociones(mociones.filter((mocion) => mocion.ID !== id));
        });
    };

    return (
        <div className="App">
            <MocionForm addMocion={addMocion} />
            <MocionesList
                mociones={mociones}
                updateMocion={updateMocion}
                deleteMocion={deleteMocion}
                addMocion={addMocion}
            />
        </div>
    );
};

export default App;
