import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const CreateMocion = () => {
    const [topico, setTopico] = useState('');
    const [tiempoDel, setTiempoDel] = useState('');
    const [cantDel, setCantDel] = useState('');
    const [tiempoTotal, setTiempoTotal] = useState('');
    const [sesion, setSesion] = useState('');
    const [fecha, setFecha] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        const newMocion = {
            Topico: topico,
            Tiempo_Del: parseInt(tiempoDel),
            Cant_Del: parseInt(cantDel),
            Tiempo_Total: parseInt(tiempoDel) * parseInt(cantDel), // Calcula el tiempo total
            Sesion: sesion,
            Fecha: new Date(fecha).toISOString(),
        };

        try {
            await axios.post('http://localhost:8080/mociones', newMocion);
            navigate('/');
        } catch (error) {
            console.error('Error creating mocion:', error);
        }
    };

    return (
        <div>
            <h2>Agregar Moción</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Topico:</label>
                    <input
                        type="text"
                        value={topico}
                        onChange={(e) => setTopico(e.target.value)}
                    />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input
                        type="number"
                        value={tiempoDel}
                        onChange={(e) => setTiempoDel(e.target.value)}
                    />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input
                        type="number"
                        value={cantDel}
                        onChange={(e) => setCantDel(e.target.value)}
                    />
                </div>
                <div>
                    <label>Sesion:</label>
                    <input
                        type="text"
                        value={sesion}
                        onChange={(e) => setSesion(e.target.value)}
                    />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input
                        type="date"
                        value={fecha}
                        onChange={(e) => setFecha(e.target.value)}
                    />
                </div>
                <button type="submit">Agregar Moción</button>
            </form>
        </div>
    );
};

export default CreateMocion;
