import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const EditMocion = ({ mociones, updateMocion }) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [mocion, setMocion] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: '',
    });

    useEffect(() => {
        const currentMocion = mociones.find((m) => m.ID === parseInt(id, 10));
        if (currentMocion) {
            setMocion(currentMocion);
        }
    }, [id, mociones]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const tiempoTotal = mocion.Tiempo_Del * mocion.Cant_Del;
        updateMocion({ ...mocion, Tiempo_Total: tiempoTotal });
        navigate('/');
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMocion({ ...mocion, [name]: value });
    };

    return (
        <div>
            <h2>Editar Moción</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Tópico:</label>
                    <input type="text" name="Topico" value={mocion.Topico} onChange={handleChange} required />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input type="number" name="Tiempo_Del" value={mocion.Tiempo_Del} onChange={handleChange} required />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input type="number" name="Cant_Del" value={mocion.Cant_Del} onChange={handleChange} required />
                </div>
                <div>
                    <label>Sesión:</label>
                    <input type="text" name="Sesion" value={mocion.Sesion} onChange={handleChange} required />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input type="date" name="Fecha" value={mocion.Fecha} onChange={handleChange} required />
                </div>
                <button type="submit">Guardar Cambios</button>
            </form>
        </div>
    );
};

export default EditMocion;
