import React from 'react';
import axios from 'axios';

const MocionesList = ({ mociones, onDelete, onEdit }) => {
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/mociones/${id}`);
            onDelete(id);
        } catch (error) {
            console.error('Error al eliminar la moci贸n:', error);
        }
    };

    return (
        <div>
            <h1>Lista de Mociones</h1>
            <table>
                <thead>
                <tr>
                    <th>T贸pico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesi贸n</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.map(mocion => (
                    <tr key={mocion.ID}>
                        <td>{mocion.Topico}</td>
                        <td>{mocion.Tiempo_Del}</td>
                        <td>{mocion.Cant_Del}</td>
                        <td>{mocion.Tiempo_Total}</td>
                        <td>{mocion.Sesion}</td>
                        <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                        <td>
                            <button onClick={() => onEdit(mocion)}>Editar</button>
                            <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default MocionesList;
