import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

function MocionesList({ mociones }) {
    const navigate = useNavigate();

    const handleDelete = (id) => {
        fetch(`http://localhost:8080/mociones/${id}`, {
            method: 'DELETE'
        })
            .then(response => response.json())
            .then(data => {
                // Actualiza la lista de mociones después de eliminar
                navigate('/');
            })
            .catch(error => console.error('Error:', error));
    };

    return (
        <div>
            <h2>Lista de Mociones</h2>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.map((mocion) => (
                    <tr key={mocion.ID}>
                        <td>{mocion.Topico}</td>
                        <td>{mocion.Tiempo_Del}</td>
                        <td>{mocion.Cant_Del}</td>
                        <td>{mocion.Tiempo_Total}</td>
                        <td>{mocion.Sesion}</td>
                        <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                        <td>
                            <Link to={`/editar/${mocion.ID}`}>Editar</Link>
                            <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                            <Link to={`/ver/${mocion.ID}`}>Ver</Link>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default MocionesList;
