import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { getMociones, deleteMocion } from '../apiService';

function MocionesList() {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        loadMociones();
    }, []);

    const loadMociones = async () => {
        try {
            const data = await getMociones();
            setMociones(data);
        } catch (error) {
            console.error('Error loading mociones:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteMocion(id);
            loadMociones(); // Reload mociones after deletion
        } catch (error) {
            console.error('Error deleting mocion:', error);
        }
    };

    return (
        <div>
            <h2>Lista de Mociones</h2>
            <Link to="/crear">Agregar Moción</Link>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.length > 0 ? (
                    mociones.map((mocion) => (
                        <tr key={mocion.ID}>
                            <td>{mocion.Topico}</td>
                            <td>{mocion.Tiempo_Del}</td>
                            <td>{mocion.Cant_Del}</td>
                            <td>{mocion.Tiempo_Total}</td>
                            <td>{mocion.Sesion}</td>
                            <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                            <td>
                                <Link to={`/ver/${mocion.ID}`}>Ver</Link> |{' '}
                                <Link to={`/editar/${mocion.ID}`}>Editar</Link> |{' '}
                                <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                            </td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="7">No hay mociones disponibles</td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
}

export default MocionesList;
