import React from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const MocionesList = ({ mociones, onFetchMociones }) => {

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/mociones/${id}`);
            onFetchMociones();
        } catch (error) {
            console.error('Error deleting mocion:', error);
        }
    };

    return (
        <div>
            {mociones.length === 0 ? (
                <p>No hay mociones disponibles</p>
            ) : (
                <table>
                    <thead>
                    <tr>
                        <th>Tópico</th>
                        <th>Tiempo por Delegado</th>
                        <th>Cantidad de Delegados</th>
                        <th>Tiempo Total</th>
                        <th>Sesión</th>
                        <th>Fecha</th>
                        <th>Acciones</th>
                    </tr>
                    </thead>
                    <tbody>
                    {mociones.map((mocion) => (
                        <tr key={mocion.ID}>
                            <td>{mocion.Topico}</td>
                            <td>{mocion.Tiempo_Del}</td>
                            <td>{mocion.Cant_Del}</td>
                            <td>{mocion.Tiempo_Total}</td>
                            <td>{mocion.Sesion}</td>
                            <td>{mocion.Fecha}</td>
                            <td>
                                <Link to={`/edit/${mocion.ID}`}>Editar</Link>
                                <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            )}
        </div>
    );
};

export default MocionesList;
