import React, { useState, useEffect } from 'react';
import MocionesList from './components/MocionesList';
import MocionForm from './components/MocionForm';
import axios from 'axios';

const App = () => {
    const [mociones, setMociones] = useState([]);
    const [selectedMocion, setSelectedMocion] = useState(null);

    useEffect(() => {
        const fetchMociones = async () => {
            try {
                const response = await axios.get('http://localhost:8080/mociones');
                console.log(response.data); // Añadir esta línea para verificar los datos
                setMociones(response.data);
            } catch (error) {
                console.error('Error al obtener las mociones:', error);
            }
        };
        fetchMociones();
    }, []);

    const addMocion = (newMocion) => {
        setMociones([...mociones, newMocion]);
    };

    const updateMocion = (updatedMocion) => {
        setMociones(mociones.map(mocion => (mocion.ID === updatedMocion.ID ? updatedMocion : mocion)));
        setSelectedMocion(null);
    };

    const deleteMocion = (id) => {
        setMociones(mociones.filter(mocion => mocion.ID !== id));
    };

    return (
        <div>
            <MocionForm onMocionAdded={addMocion} onMocionUpdated={updateMocion} selectedMocion={selectedMocion} />
            <MocionesList mociones={mociones} onDelete={deleteMocion} onEdit={setSelectedMocion} />
        </div>
    );
};

export default App;
