import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { fetchMociones, deleteMocion } from '../apiService';

const MocionesList = () => {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        loadMociones();
    }, []);

    const loadMociones = async () => {
        try {
            const data = await fetchMociones();
            setMociones(data);
        } catch (error) {
            console.error('Error loading mociones:', error);
        }
    };

    const handleDelete = async (id) => {
        await deleteMocion(id);
        loadMociones();
    };

    return (
        <div>
            <h1>Lista de Mociones</h1>
            <Link to="/add" className="btn btn-primary">Agregar Moción</Link>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.length > 0 ? (
                    mociones.map(mocion => (
                        <tr key={mocion.ID}>
                            <td>{mocion.Topico}</td>
                            <td>{mocion.Tiempo_Del}</td>
                            <td>{mocion.Cant_Del}</td>
                            <td>{mocion.Tiempo_Total}</td>
                            <td>{mocion.Sesion}</td>
                            <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                            <td>
                                <Link to={`/edit/${mocion.ID}`} className="btn btn-secondary">Editar</Link>
                                <button onClick={() => handleDelete(mocion.ID)} className="btn btn-danger">Eliminar</button>
                                <Link to={`/view/${mocion.ID}`} className="btn btn-info">Ver</Link>
                            </td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="7">No hay mociones disponibles</td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
};

export default MocionesList;
