import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { getMocionById, updateMocion } from '../apiService';

function EditMocion() {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const data = await getMocionById(id);
                setMocion(data);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    const handleChange = (e) => {
        setMocion({ ...mocion, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            mocion.Tiempo_Del = parseInt(mocion.Tiempo_Del);
            mocion.Cant_Del = parseInt(mocion.Cant_Del);
            mocion.Tiempo_Total = mocion.Tiempo_Del * mocion.Cant_Del;
            await updateMocion(id, mocion);
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    if (!mocion) return <div>Loading...</div>;

    return (
        <div>
            <h2>Editar Moción</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Tópico:
                    <input type="text" name="Topico" value={mocion.Topico} onChange={handleChange} />
                </label>
                <label>
                    Tiempo por Delegado:
                    <input type="number" name="Tiempo_Del" value={mocion.Tiempo_Del} onChange={handleChange} />
                </label>
                <label>
                    Cantidad de Delegados:
                    <input type="number" name="Cant_Del" value={mocion.Cant_Del} onChange={handleChange} />
                </label>
                <label>
                    Sesión:
                    <input type="text" name="Sesion" value={mocion.Sesion} onChange={handleChange} />
                </label>
                <label>
                    Fecha:
                    <input type="date" name="Fecha" value={mocion.Fecha} onChange={handleChange} />
                </label>
                <button type="submit">Guardar Cambios</button>
            </form>
        </div>
    );
}

export default EditMocion;
