import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const CreateMocion = ({ onFetchMociones }) => {
    const [topico, setTopico] = useState('');
    const [tiempoDel, setTiempoDel] = useState('');
    const [cantDel, setCantDel] = useState('');
    const [sesion, setSesion] = useState('');
    const [fecha, setFecha] = useState('');
    const history = useHistory();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const tiempoTotal = parseInt(tiempoDel) * parseInt(cantDel);
        try {
            await axios.post('http://localhost:8080/mociones', {
                Topico: topico,
                Tiempo_Del: parseInt(tiempoDel),
                Cant_Del: parseInt(cantDel),
                Tiempo_Total: tiempoTotal,
                Sesion: sesion,
                Fecha: fecha
            });
            onFetchMociones();
            history.push('/');
        } catch (error) {
            console.error('Error creating mocion:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Tópico:</label>
            <input type="text" value={topico} onChange={(e) => setTopico(e.target.value)} />
            <label>Tiempo por Delegado:</label>
            <input type="number" value={tiempoDel} onChange={(e) => setTiempoDel(e.target.value)} />
            <label>Cantidad de Delegados:</label>
            <input type="number" value={cantDel} onChange={(e) => setCantDel(e.target.value)} />
            <label>Sesión:</label>
            <input type="text" value={sesion} onChange={(e) => setSesion(e.target.value)} />
            <label>Fecha:</label>
            <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} />
            <button type="submit">Agregar Moción</button>
        </form>
    );
};

export default CreateMocion;
