import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import { createMocion } from '../apiService';

function CreateMocion() {
    const [mocion, setMocion] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: '',
    });
    const history = useHistory();

    const handleChange = (e) => {
        setMocion({ ...mocion, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            mocion.Tiempo_Del = parseInt(mocion.Tiempo_Del);
            mocion.Cant_Del = parseInt(mocion.Cant_Del);
            mocion.Tiempo_Total = mocion.Tiempo_Del * mocion.Cant_Del;
            await createMocion(mocion);
            history.push('/');
        } catch (error) {
            console.error('Error creating mocion:', error);
        }
    };

    return (
        <div>
            <h2>Crear Moci贸n</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    T贸pico:
                    <input type="text" name="Topico" value={mocion.Topico} onChange={handleChange} />
                </label>
                <label>
                    Tiempo por Delegado:
                    <input type="number" name="Tiempo_Del" value={mocion.Tiempo_Del} onChange={handleChange} />
                </label>
                <label>
                    Cantidad de Delegados:
                    <input type="number" name="Cant_Del" value={mocion.Cant_Del} onChange={handleChange} />
                </label>
                <label>
                    Sesi贸n:
                    <input type="text" name="Sesion" value={mocion.Sesion} onChange={handleChange} />
                </label>
                <label>
                    Fecha:
                    <input type="date" name="Fecha" value={mocion.Fecha} onChange={handleChange} />
                </label>
                <button type="submit">Agregar Moci贸n</button>
            </form>
        </div>
    );
}

export default CreateMocion;
