import React from 'react';
import axios from 'axios';

const MocionesList = ({ mociones, onDelete, onUpdate }) => {
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/mociones/${id}`);
            onDelete(id);
        } catch (error) {
            console.error('Error al eliminar la moci贸n:', error);
        }
    };

    const handleUpdate = async (mocion) => {
        try {
            const response = await axios.put(`http://localhost:8080/mociones/${mocion.ID}`, mocion);
            onUpdate(response.data);
        } catch (error) {
            console.error('Error al actualizar la moci贸n:', error);
        }
    };

    return (
        <div>
            <h1>Lista de Mociones</h1>
            <ul>
                {mociones.map(mocion => (
                    <li key={mocion.ID}>
                        {mocion.Topico}
                        <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                        <button onClick={() => handleUpdate(mocion)}>Actualizar</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MocionesList;
