import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import apiService from '../apiService';

function MocionesList() {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        async function fetchMociones() {
            try {
                const mocionesData = await apiService.getMociones();
                setMociones(mocionesData);
            } catch (error) {
                console.error('Error fetching mociones:', error);
            }
        }
        fetchMociones();
    }, []);

    const handleDelete = async (id) => {
        try {
            await apiService.deleteMocion(id);
            setMociones(mociones.filter(mocion => mocion.ID !== id));
        } catch (error) {
            console.error('Error deleting mocion:', error);
        }
    };

    return (
        <div>
            <h1>Lista de Mociones</h1>
            <Link to="/create">Agregar Moción</Link>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.length > 0 ? (
                    mociones.map(mocion => (
                        <tr key={mocion.ID}>
                            <td>{mocion.Topico}</td>
                            <td>{mocion.Tiempo_Del}</td>
                            <td>{mocion.Cant_Del}</td>
                            <td>{mocion.Tiempo_Total}</td>
                            <td>{mocion.Sesion}</td>
                            <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                            <td>
                                <Link to={`/edit/${mocion.ID}`}>Editar</Link>
                                <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                                <Link to={`/view/${mocion.ID}`}>Ver</Link>
                            </td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="7">No hay mociones disponibles</td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
}

export default MocionesList;
