import React, { useState } from 'react';
import MocionDetails from './MocionDetails';
import MocionForm from './MocionForm';

const MocionesList = ({ mociones, updateMocion, deleteMocion, addMocion }) => {
    const [selectedMocion, setSelectedMocion] = useState(null);
    const [isEditing, setIsEditing] = useState(false);

    const handleEdit = (mocion) => {
        setSelectedMocion(mocion);
        setIsEditing(true);
    };

    const handleView = (mocion) => {
        setSelectedMocion(mocion);
        setIsEditing(false);
    };

    return (
        <div>
            <h2>Lista de Mociones</h2>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.map((mocion) => (
                    <tr key={mocion.ID}>
                        <td>{mocion.Topico}</td>
                        <td>{mocion.Tiempo_Del}</td>
                        <td>{mocion.Cant_Del}</td>
                        <td>{mocion.Tiempo_Total}</td>
                        <td>{mocion.Sesion}</td>
                        <td>{mocion.Fecha}</td>
                        <td>
                            <button onClick={() => handleView(mocion)}>Ver</button>
                            <button onClick={() => handleEdit(mocion)}>Editar</button>
                            <button onClick={() => deleteMocion(mocion.ID)}>Eliminar</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            {selectedMocion && !isEditing && (
                <div className="modal">
                    <MocionDetails mocion={selectedMocion} />
                </div>
            )}

            {isEditing && selectedMocion && (
                <div className="modal">
                    <MocionForm
                        initialData={selectedMocion}
                        onSubmit={(updatedMocion) => {
                            updateMocion(updatedMocion);
                            setIsEditing(false);
                        }}
                    />
                </div>
            )}
        </div>
    );
};

export default MocionesList;
