import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

const EditMocion = ({ mociones, updateMocion }) => {
    const { id } = useParams();
    const mocion = mociones.find((m) => m.id === parseInt(id));
    const [topico, setTopico] = useState(mocion.topico);
    const [tiempoDel, setTiempoDel] = useState(mocion.tiempoDel);
    const [cantDel, setCantDel] = useState(mocion.cantDel);
    const [sesion, setSesion] = useState(mocion.sesion);
    const [fecha, setFecha] = useState(mocion.fecha);
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();
        const tiempoTotal = tiempoDel * cantDel;
        const updatedMocion = {
            ...mocion,
            topico,
            tiempoDel: parseInt(tiempoDel),
            cantDel: parseInt(cantDel),
            tiempoTotal,
            sesion,
            fecha,
        };
        updateMocion(updatedMocion);
        navigate('/');
    };

    return (
        <div style={{ padding: '20px', maxWidth: '500px', margin: '0 auto' }}>
            <h2>Editar Mocion</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Tópico:</label>
                    <input type="text" value={topico} onChange={(e) => setTopico(e.target.value)} required />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input type="number" value={tiempoDel} onChange={(e) => setTiempoDel(e.target.value)} required />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input type="number" value={cantDel} onChange={(e) => setCantDel(e.target.value)} required />
                </div>
                <div>
                    <label>Sesión:</label>
                    <input type="text" value={sesion} onChange={(e) => setSesion(e.target.value)} required />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} required />
                </div>
                <button type="submit" style={{ marginTop: '10px' }}>Guardar Cambios</button>
            </form>
        </div>
    );
};

export default EditMocion;
