import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const App = () => {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        fetch('http://localhost:8080/mociones')
            .then(response => response.json())
            .then(data => setMociones(data))
            .catch(error => console.error('Error al cargar las mociones:', error));
    }, []);

    const deleteMocion = (id) => {
        fetch(`http://localhost:8080/mociones/${id}`, {
            method: 'DELETE',
        })
            .then(() => {
                setMociones(mociones.filter(m => m.ID !== id));
            })
            .catch(error => console.error('Error al eliminar la moción:', error));
    };

    return (
        <div>
            <h1>Lista de Mociones</h1>
            <Link to="/create">Agregar Moción</Link>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.map(mocion => (
                    <tr key={mocion.ID}>
                        <td>{mocion.Topico}</td>
                        <td>{mocion.Tiempo_Del}</td>
                        <td>{mocion.Cant_Del}</td>
                        <td>{mocion.Tiempo_Total}</td>
                        <td>{mocion.Sesion}</td>
                        <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                        <td>
                            <Link to={`/edit/${mocion.ID}`}>Editar</Link>
                            <button onClick={() => deleteMocion(mocion.ID)}>Eliminar</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default App;
