import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

function VerMocion() {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:8080/mociones/${id}`)
            .then((response) => response.json())
            .then((data) => setMocion(data))
            .catch((error) => console.error('Error fetching mocion:', error));
    }, [id]);

    if (!mocion) return <div>Loading...</div>;

    return (
        <div>
            <h2>Delegados de la Moción: {mocion.Topico}</h2>
            {Array.isArray(mocion.Delegado) ? (
                mocion.Delegado.map((delegado) => (
                    <div key={delegado.ID}>
                        <p>Nombre: {delegado.Nombre}</p>
                        <p>Apellido: {delegado.Apellido}</p>
                        <p>Delegación: {delegado.Delegacion}</p>
                        {/* Otros detalles de los delegados */}
                    </div>
                ))
            ) : (
                <p>No hay delegados disponibles</p>
            )}
        </div>
    );
}

export default VerMocion;
