import React, { useState } from 'react';

const MocionForm = ({ addMocion }) => {
    const [topico, setTopico] = useState('');
    const [tiempoDel, setTiempoDel] = useState('');
    const [cantDel, setCantDel] = useState('');
    const [sesion, setSesion] = useState('');
    const [fecha, setFecha] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const nuevaMocion = {
            Topico: topico,
            Tiempo_Del: parseInt(tiempoDel),
            Cant_Del: parseInt(cantDel),
            Tiempo_Total: parseInt(tiempoDel) * parseInt(cantDel),
            Sesion: sesion,
            Fecha: fecha,
        };
        addMocion(nuevaMocion);
        setTopico('');
        setTiempoDel('');
        setCantDel('');
        setSesion('');
        setFecha('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder="Tópico"
                value={topico}
                onChange={(e) => setTopico(e.target.value)}
            />
            <input
                type="number"
                placeholder="Tiempo por Delegado"
                value={tiempoDel}
                onChange={(e) => setTiempoDel(e.target.value)}
            />
            <input
                type="number"
                placeholder="Cantidad de Delegados"
                value={cantDel}
                onChange={(e) => setCantDel(e.target.value)}
            />
            <input
                type="text"
                placeholder="Sesión"
                value={sesion}
                onChange={(e) => setSesion(e.target.value)}
            />
            <input
                type="date"
                value={fecha}
                onChange={(e) => setFecha(e.target.value)}
            />
            <button type="submit">Agregar Moción</button>
        </form>
    );
};

export default MocionForm;
