import React from 'react';

const MocionesList = ({ mociones, addMocion, updateMocion, deleteMocion }) => {
    const handleCreate = () => {
        const newWindow = window.open('', '_blank', 'width=600,height=400');
        newWindow.document.write('<div id="root"></div>');
        newWindow.document.title = "Crear Moción";
        newWindow.document.write(`
      <script type="text/babel">
        ReactDOM.render(
          React.createElement(CreateMocion, { addMocion: ${addMocion.toString()} }),
          document.getElementById('root')
        );
      </script>
    `);
    };

    const handleEdit = (mocion) => {
        const newWindow = window.open('', '_blank', 'width=600,height=400');
        newWindow.document.write('<div id="root"></div>');
        newWindow.document.title = "Editar Moción";
        newWindow.document.write(`
      <script type="text/babel">
        ReactDOM.render(
          React.createElement(EditMocion, { mocion: ${JSON.stringify(mocion)}, updateMocion: ${updateMocion.toString()} }),
          document.getElementById('root')
        );
      </script>
    `);
    };

    const handleView = (mocion) => {
        const newWindow = window.open('', '_blank', 'width=600,height=400');
        newWindow.document.write('<div id="root"></div>');
        newWindow.document.title = "Ver Moción";
        newWindow.document.write(`
      <script type="text/babel">
        ReactDOM.render(
          React.createElement(ViewMocion, { mocion: ${JSON.stringify(mocion)} }),
          document.getElementById('root')
        );
      </script>
    `);
    };

    return (
        <div>
            <h2>Lista de Mociones</h2>
            <button onClick={handleCreate}>Agregar Moción</button>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.map((mocion) => (
                    <tr key={mocion.ID}>
                        <td>{mocion.Topico}</td>
                        <td>{mocion.Tiempo_Del}</td>
                        <td>{mocion.Cant_Del}</td>
                        <td>{mocion.Tiempo_Del * mocion.Cant_Del}</td>
                        <td>{mocion.Sesion}</td>
                        <td>{mocion.Fecha}</td>
                        <td>
                            <button onClick={() => handleView(mocion)}>Ver</button>
                            <button onClick={() => handleEdit(mocion)}>Editar</button>
                            <button onClick={() => deleteMocion(mocion.ID)}>Eliminar</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default MocionesList;
