import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const ViewMocion = () => {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/mociones/${id}`);
                setMocion(response.data);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    if (!mocion) return <div>Cargando...</div>;

    return (
        <div>
            <h2>Delegados de la Moción: {mocion.Topico}</h2>
            <button onClick={() => navigate('/')}>Volver</button>
            <table>
                <thead>
                <tr>
                    <th>Nombre</th>
                    <th>Apellido</th>
                    <th>Delegación</th>
                    <th>Correo</th>
                </tr>
                </thead>
                <tbody>
                {mocion.Delegado.map((delegado) => (
                    <tr key={delegado.ID}>
                        <td>{delegado.Nombre}</td>
                        <td>{delegado.Apellido}</td>
                        <td>{delegado.Delegacion}</td>
                        <td>{delegado.Correo}</td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
};

export default ViewMocion;
