import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const VerMocion = () => {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/mociones/${id}`);
                setMocion(response.data);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    if (!mocion) return <div>Loading...</div>;

    return (
        <div>
            <h2>Delegados de la Moci√≥n: {mocion.Topico}</h2>
            {mocion.Delegados && mocion.Delegados.length > 0 ? (
                <ul>
                    {mocion.Delegados.map(delegado => (
                        <li key={delegado.ID}>
                            {delegado.Nombre} {delegado.Apellido} - {delegado.Delegacion}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No hay delegados disponibles</p>
            )}
        </div>
    );
};

export default VerMocion;
