import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MocionesList = () => {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        const fetchMociones = async () => {
            try {
                const response = await axios.get('http://localhost:8080/mociones');
                setMociones(response.data);
            } catch (error) {
                console.error('Error al obtener las mociones:', error);
            }
        };
        fetchMociones();
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8080/mociones/${id}`);
            setMociones(mociones.filter(mocion => mocion.ID !== id));
        } catch (error) {
            console.error('Error al eliminar la moción:', error);
        }
    };

    return (
        <div>
            <h1>Lista de Mociones</h1>
            <ul>
                {mociones.map(mocion => (
                    <li key={mocion.ID}>
                        {mocion.Topico}
                        <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default MocionesList;
