import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';
import axios from 'axios';
import './App.css';

import MocionesList from './components/MocionesList';
import CreateMocion from './components/CreateMocion';
import EditMocion from './components/EditMocion';

const App = () => {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        fetchMociones();
    }, []);

    const fetchMociones = async () => {
        try {
            const response = await axios.get('http://localhost:8080/mociones');
            setMociones(response.data);
        } catch (error) {
            console.error('Error fetching mociones:', error);
        }
    };

    return (
        <Router>
            <div className="App">
                <nav>
                    <Link to="/">Lista de Mociones</Link> | <Link to="/create">Agregar Mocion</Link>
                </nav>
                <h1>Lista de Mociones</h1>
                <Switch>
                    <Route exact path="/">
                        <MocionesList mociones={mociones} onFetchMociones={fetchMociones} />
                    </Route>
                    <Route path="/create">
                        <CreateMocion onFetchMociones={fetchMociones} />
                    </Route>
                    <Route path="/edit/:id">
                        <EditMocion onFetchMociones={fetchMociones} />
                    </Route>
                </Switch>
            </div>
        </Router>
    );
};

export default App;
