// src/components/EditMocion.jsx
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { fetchMocionById, updateMocion } from '../apiService';

const EditMocion = () => {
    const { id } = useParams();
    const [form, setForm] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: '',
    });
    const navigate = useNavigate();

    useEffect(() => {
        fetchMocionById(id)
            .then((data) => setForm(data))
            .catch((error) => console.error('Error fetching mocion:', error));
    }, [id]);

    const handleChange = (e) => {
        setForm({ ...form, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateMocion(id, { ...form, Tiempo_Total: form.Tiempo_Del * form.Cant_Del });
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <div>
                <label>Tópico:</label>
                <input type="text" name="Topico" value={form.Topico} onChange={handleChange} />
            </div>
            <div>
                <label>Tiempo por Delegado:</label>
                <input type="number" name="Tiempo_Del" value={form.Tiempo_Del} onChange={handleChange} />
            </div>
            <div>
                <label>Cantidad de Delegados:</label>
                <input type="number" name="Cant_Del" value={form.Cant_Del} onChange={handleChange} />
            </div>
            <div>
                <label>Sesión:</label>
                <input type="text" name="Sesion" value={form.Sesion} onChange={handleChange} />
            </div>
            <div>
                <label>Fecha:</label>
                <input type="date" name="Fecha" value={form.Fecha} onChange={handleChange} />
            </div>
            <button type="submit">Guardar Cambios</button>
        </form>
    );
};

export default EditMocion;
