import React from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MocionesList from './components/MocionesList';
import CreateMocion from './components/CreateMocion';
import EditMocion from './components/EditMocion';
import ViewMocion from './components/ViewMocion';

const App = () => {
    const [mociones, setMociones] = React.useState([]);

    const addMocion = (mocion) => {
        setMociones([...mociones, { ...mocion, id: mociones.length + 1 }]);
    };

    const updateMocion = (updatedMocion) => {
        setMociones(mociones.map(m => m.id === updatedMocion.id ? updatedMocion : m));
    };

    const deleteMocion = (id) => {
        setMociones(mociones.filter(m => m.id !== id));
    };

    return (
        <Router>
            <div>
                <nav>
                    <Link to="/">Lista de Mociones</Link> |
                    <Link to="/create">Agregar Moci√≥n</Link>
                </nav>
                <Routes>
                    <Route path="/" element={<MocionesList mociones={mociones} deleteMocion={deleteMocion} />} />
                    <Route path="/create" element={<CreateMocion addMocion={addMocion} />} />
                    <Route path="/edit/:id" element={<EditMocion mociones={mociones} updateMocion={updateMocion} />} />
                    <Route path="/view/:id" element={<ViewMocion mociones={mociones} />} />
                </Routes>
            </div>
        </Router>
    );
};

export default App;
