import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const EditMocion = () => {
    const { id } = useParams();
    const [topico, setTopico] = useState('');
    const [tiempoDel, setTiempoDel] = useState('');
    const [cantDel, setCantDel] = useState('');
    const [tiempoTotal, setTiempoTotal] = useState('');
    const [sesion, setSesion] = useState('');
    const [fecha, setFecha] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/mociones/${id}`);
                const mocion = response.data;
                setTopico(mocion.Topico);
                setTiempoDel(mocion.Tiempo_Del);
                setCantDel(mocion.Cant_Del);
                setTiempoTotal(mocion.Tiempo_Total);
                setSesion(mocion.Sesion);
                setFecha(mocion.Fecha.split('T')[0]); // Format date for input[type="date"]
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const updatedMocion = {
            Topico: topico,
            Tiempo_Del: parseInt(tiempoDel),
            Cant_Del: parseInt(cantDel),
            Tiempo_Total: parseInt(tiempoDel) * parseInt(cantDel), // Calcula el tiempo total
            Sesion: sesion,
            Fecha: new Date(fecha).toISOString(),
        };

        try {
            await axios.put(`http://localhost:8080/mociones/${id}`, updatedMocion);
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    return (
        <div>
            <h2>Editar Moci√≥n</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Topico:</label>
                    <input
                        type="text"
                        value={topico}
                        onChange={(e) => setTopico(e.target.value)}
                    />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input
                        type="number"
                        value={tiempoDel}
                        onChange={(e) => setTiempoDel(e.target.value)}
                    />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input
                        type="number"
                        value={cantDel}
                        onChange={(e) => setCantDel(e.target.value)}
                    />
                </div>
                <div>
                    <label>Sesion:</label>
                    <input
                        type="text"
                        value={sesion}
                        onChange={(e) => setSesion(e.target.value)}
                    />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input
                        type="date"
                        value={fecha}
                        onChange={(e) => setFecha(e.target.value)}
                    />
                </div>
                <button type="submit">Guardar Cambios</button>
            </form>
        </div>
    );
};

export default EditMocion;
