// src/apiService.js
const API_URL = 'http://localhost:8080/mociones';

export async function fetchMociones() {
    const response = await fetch(API_URL);
    if (!response.ok) {
        throw new Error('Error fetching mociones');
    }
    return response.json();
}

export async function createMocion(mocion) {
    const response = await fetch(API_URL, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(mocion),
    });
    if (!response.ok) {
        throw new Error('Error creating mocion');
    }
    return response.json();
}

export async function updateMocion(id, mocion) {
    const response = await fetch(`${API_URL}/${id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(mocion),
    });
    if (!response.ok) {
        throw new Error('Error updating mocion');
    }
    return response.json();
}

export async function deleteMocion(id) {
    const response = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE',
    });
    if (!response.ok) {
        throw new Error('Error deleting mocion');
    }
    return response.json();
}

export async function fetchMocionById(id) {
    const response = await fetch(`${API_URL}/${id}`);
    if (!response.ok) {
        throw new Error('Error fetching mocion by ID');
    }
    return response.json();
}
