import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import MocionesList from './components/MocionesList';
import CreateMocion from './components/CreateMocion';
import EditMocion from './components/EditMocion';
import VerMocion from './components/VerMocion';
import { fetchMociones, addMocion, updateMocion, deleteMocion } from './apiService';
import './App.css';

function App() {
    const [mociones, setMociones] = useState([]);

    useEffect(() => {
        fetchMociones()
            .then(data => setMociones(data))
            .catch(error => console.error('Error fetching mociones:', error));
    }, []);

    const handleAddMocion = (newMocion) => {
        addMocion(newMocion)
            .then(data => setMociones([...mociones, data]))
            .catch(error => console.error('Error adding mocion:', error));
    };

    const handleUpdateMocion = (updatedMocion) => {
        updateMocion(updatedMocion)
            .then(data => {
                setMociones(mociones.map(mocion => (mocion.ID === data.ID ? data : mocion)));
            })
            .catch(error => console.error('Error updating mocion:', error));
    };

    const handleDeleteMocion = (id) => {
        deleteMocion(id)
            .then(() => fetchMociones().then(data => setMociones(data)))
            .catch(error => console.error('Error deleting mocion:', error));
    };

    return (
        <Router>
            <div>
                <nav>
                    <Link to="/">Lista de Mociones</Link> | <Link to="/crear">Agregar Moci√≥n</Link>
                </nav>
                <Routes>
                    <Route path="/" element={<MocionesList mociones={mociones} deleteMocion={handleDeleteMocion} />} />
                    <Route path="/crear" element={<CreateMocion addMocion={handleAddMocion} />} />
                    <Route path="/editar/:id" element={<EditMocion mociones={mociones} updateMocion={handleUpdateMocion} />} />
                    <Route path="/ver/:id" element={<VerMocion />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
