const apiUrl = 'http://localhost:8080/mociones';

async function getMociones() {
    const response = await fetch(apiUrl);
    if (!response.ok) {
        throw new Error('Failed to fetch mociones');
    }
    return response.json();
}

async function getMocion(id) {
    const response = await fetch(`${apiUrl}/${id}`);
    if (!response.ok) {
        throw new Error(`Failed to fetch mocion with id ${id}`);
    }
    return response.json();
}

async function createMocion(mocion) {
    const response = await fetch(apiUrl, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(mocion)
    });
    if (!response.ok) {
        throw new Error('Failed to create mocion');
    }
    return response.json();
}

async function updateMocion(id, mocion) {
    const response = await fetch(`${apiUrl}/${id}`, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json'
        },
        body: JSON.stringify(mocion)
    });
    if (!response.ok) {
        throw new Error(`Failed to update mocion with id ${id}`);
    }
    return response.json();
}

async function deleteMocion(id) {
    const response = await fetch(`${apiUrl}/${id}`, {
        method: 'DELETE'
    });
    if (!response.ok) {
        throw new Error(`Failed to delete mocion with id ${id}`);
    }
    return response.json();
}

export default {
    getMociones,
    getMocion,
    createMocion,
    updateMocion,
    deleteMocion
};
