import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

const EditMocion = () => {
    const { id } = useParams();
    const [formData, setFormData] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: '',
    });

    const navigate = useNavigate();

    useEffect(() => {
        fetch(`http://localhost:8080/mociones/${id}`)
            .then(response => response.json())
            .then(data => setFormData(data))
            .catch(error => console.error('Error al cargar la moción:', error));
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch(`http://localhost:8080/mociones/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(formData),
        })
            .then(response => response.json())
            .then(data => {
                console.log('Moción actualizada:', data);
                navigate('/');
            })
            .catch(error => console.error('Error al actualizar la moción:', error));
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value,
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Tópico:
                <input type="text" name="Topico" value={formData.Topico} onChange={handleChange} required />
            </label>
            <label>
                Tiempo por Delegado:
                <input type="number" name="Tiempo_Del" value={formData.Tiempo_Del} onChange={handleChange} required />
            </label>
            <label>
                Cantidad de Delegados:
                <input type="number" name="Cant_Del" value={formData.Cant_Del} onChange={handleChange} required />
            </label>
            <label>
                Sesión:
                <input type="text" name="Sesion" value={formData.Sesion} onChange={handleChange} required />
            </label>
            <label>
                Fecha:
                <input type="date" name="Fecha" value={formData.Fecha} onChange={handleChange} required />
            </label>
            <button type="submit">Guardar cambios</button>
        </form>
    );
};

export default EditMocion;
