import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { updateMocion, fetchMociones } from '../apiService';

const EditMocion = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [mocion, setMocion] = useState(null);

    useEffect(() => {
        const loadMocion = async () => {
            const mociones = await fetchMociones();
            const mocion = mociones.find((m) => m.ID === parseInt(id));
            setMocion(mocion);
        };
        loadMocion();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        await updateMocion(id, mocion);
        navigate('/');
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMocion({ ...mocion, [name]: value });
    };

    if (!mocion) {
        return <div>Cargando...</div>;
    }

    return (
        <div>
            <h2>Editar Moción</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Tópico:</label>
                    <input type="text" name="Topico" value={mocion.Topico} onChange={handleChange} />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input type="number" name="Tiempo_Del" value={mocion.Tiempo_Del} onChange={handleChange} />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input type="number" name="Cant_Del" value={mocion.Cant_Del} onChange={handleChange} />
                </div>
                <div>
                    <label>Sesión:</label>
                    <input type="text" name="Sesion" value={mocion.Sesion} onChange={handleChange} />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input type="date" name="Fecha" value={mocion.Fecha} onChange={handleChange} />
                </div>
                <button type="submit" className="btn btn-primary">Guardar Cambios</button>
            </form>
        </div>
    );
};

export default EditMocion;
