import React, { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

const EditMocion = ({ onFetchMociones }) => {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/mociones/${id}`);
                setMocion(response.data);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await axios.put(`http://localhost:8081/mociones/${id}`, mocion);
            onFetchMociones();
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    if (!mocion) return <div>Cargando...</div>;

    return (
        <div>
            <h2>Editar Moción</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Tópico:</label>
                    <input type="text" value={mocion.Topico} onChange={(e) => setMocion({ ...mocion, Topico: e.target.value })} required />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input type="number" value={mocion.Tiempo_Del} onChange={(e) => setMocion({ ...mocion, Tiempo_Del: e.target.value })} required />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input type="number" value={mocion.Cant_Del} onChange={(e) => setMocion({ ...mocion, Cant_Del: e.target.value })} required />
                </div>
                <div>
                    <label>Sesión:</label>
                    <input type="text" value={mocion.Sesion} onChange={(e) => setMocion({ ...mocion, Sesion: e.target.value })} required />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input type="date" value={mocion.Fecha} onChange={(e) => setMocion({ ...mocion, Fecha: e.target.value })} required />
                </div>
                <button type="submit">Guardar Cambios</button>
            </form>
        </div>
    );
};

export default EditMocion;
