import React, { useState } from 'react';
import MocionForm from './MocionForm';
import MocionDetails from './MocionDetails';

const MocionesList = ({ mociones, addMocion, updateMocion, deleteMocion }) => {
    const [selectedMocion, setSelectedMocion] = useState(null);
    const [isEditing, setIsEditing] = useState(false);
    const [isViewing, setIsViewing] = useState(false);
    const [showForm, setShowForm] = useState(false);

    const handleEdit = (mocion) => {
        setSelectedMocion(mocion);
        setIsEditing(true);
        setShowForm(true);
    };

    const handleView = (mocion) => {
        setSelectedMocion(mocion);
        setIsViewing(true);
    };

    const handleClose = () => {
        setSelectedMocion(null);
        setIsEditing(false);
        setIsViewing(false);
        setShowForm(false);
    };

    return (
        <div>
            <h2>Lista de Mociones</h2>
            <button onClick={() => setShowForm(true)}>Agregar Moción</button>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.map((mocion) => (
                    <tr key={mocion.ID}>
                        <td>{mocion.Topico}</td>
                        <td>{mocion.Tiempo_Del}</td>
                        <td>{mocion.Cant_Del}</td>
                        <td>{mocion.Tiempo_Del * mocion.Cant_Del}</td>
                        <td>{mocion.Sesion}</td>
                        <td>{mocion.Fecha}</td>
                        <td>
                            <button onClick={() => handleView(mocion)}>Ver</button>
                            <button onClick={() => handleEdit(mocion)}>Editar</button>
                            <button onClick={() => deleteMocion(mocion.ID)}>Eliminar</button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            {showForm && (
                <MocionForm
                    initialData={isEditing ? selectedMocion : null}
                    onSubmit={isEditing ? updateMocion : addMocion}
                    onClose={handleClose}
                />
            )}

            {isViewing && selectedMocion && (
                <div className="modal">
                    <MocionDetails mocion={selectedMocion} onClose={handleClose} />
                </div>
            )}
        </div>
    );
};

export default MocionesList;
