import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ViewMocion = () => {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);

    useEffect(() => {
        fetchMocion();
    }, []);

    const fetchMocion = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/mociones/${id}`);
            setMocion(response.data);
        } catch (error) {
            console.error('Error fetching mocion:', error);
        }
    };

    if (!mocion) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h2>{mocion.Topico}</h2>
            <p>Tiempo por Delegado: {mocion.Tiempo_Del}</p>
            <p>Cantidad de Delegados: {mocion.Cant_Del}</p>
            <p>Tiempo Total: {mocion.Tiempo_Total}</p>
            <p>Sesi√≥n: {mocion.Sesion}</p>
            <p>Fecha: {new Date(mocion.Fecha).toLocaleDateString()}</p>
            <h3>Delegados</h3>
            <ul>
                {Array.isArray(mocion.Delegado) ? (
                    mocion.Delegado.map((delegado) => (
                        <li key={delegado.ID}>{delegado.Nombre} {delegado.Apellido}</li>
                    ))
                ) : (
                    <li>No hay delegados disponibles</li>
                )}
            </ul>
        </div>
    );
};

export default ViewMocion;
