import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditMocion = ({ onFetchMociones }) => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [mocion, setMocion] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: '',
    });

    useEffect(() => {
        fetchMocion();
    }, [id]);

    const fetchMocion = async () => {
        try {
            const response = await axios.get(`http://localhost:8080/mociones/${id}`);
            setMocion(response.data);
        } catch (error) {
            console.error('Error fetching mocion:', error);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const tiempoTotal = parseInt(mocion.Tiempo_Del) * parseInt(mocion.Cant_Del);
        try {
            await axios.put(`http://localhost:8080/mociones/${id}`, {
                ...mocion,
                Tiempo_Total: tiempoTotal,
            });
            onFetchMociones();
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>Tópico:</label>
            <input
                type="text"
                value={mocion.Topico}
                onChange={(e) => setMocion({ ...mocion, Topico: e.target.value })}
            />
            <label>Tiempo por Delegado:</label>
            <input
                type="number"
                value={mocion.Tiempo_Del}
                onChange={(e) => setMocion({ ...mocion, Tiempo_Del: e.target.value })}
            />
            <label>Cantidad de Delegados:</label>
            <input
                type="number"
                value={mocion.Cant_Del}
                onChange={(e) => setMocion({ ...mocion, Cant_Del: e.target.value })}
            />
            <label>Sesión:</label>
            <input
                type="text"
                value={mocion.Sesion}
                onChange={(e) => setMocion({ ...mocion, Sesion: e.target.value })}
            />
            <label>Fecha:</label>
            <input
                type="date"
                value={mocion.Fecha}
                onChange={(e) => setMocion({ ...mocion, Fecha: e.target.value })}
            />
            <button type="submit">Actualizar Moción</button>
        </form>
    );
};

export default EditMocion;
