const API_URL = 'http://localhost:8080/mociones';

export const getMociones = async () => {
    const response = await fetch(API_URL);
    if (!response.ok) {
        throw new Error('Failed to fetch mociones');
    }
    return await response.json();
};

export const getMocionById = async (id) => {
    const response = await fetch(`${API_URL}/${id}`);
    if (!response.ok) {
        throw new Error('Failed to fetch mocion');
    }
    return await response.json();
};

export const createMocion = async (mocion) => {
    const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(mocion),
    });
    if (!response.ok) {
        throw new Error('Failed to create mocion');
    }
    return await response.json();
};

export const updateMocion = async (id, mocion) => {
    const response = await fetch(`${API_URL}/${id}`, {
        method: 'PUT',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(mocion),
    });
    if (!response.ok) {
        throw new Error('Failed to update mocion');
    }
    return await response.json();
};

export const deleteMocion = async (id) => {
    const response = await fetch(`${API_URL}/${id}`, {
        method: 'DELETE',
    });
    if (!response.ok) {
        throw new Error('Failed to delete mocion');
    }
};
