import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

const MocionesList = ({ mociones, onFetchMociones }) => {
    const navigate = useNavigate();

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8081/mociones/${id}`);
            onFetchMociones();
        } catch (error) {
            console.error('Error deleting mocion:', error);
        }
    };

    return (
        <div>
            <h2>Lista de Mociones</h2>
            <Link to="/create">Agregar Moción</Link>
            <table>
                <thead>
                <tr>
                    <th>Tópico</th>
                    <th>Tiempo por Delegado</th>
                    <th>Cantidad de Delegados</th>
                    <th>Tiempo Total</th>
                    <th>Sesión</th>
                    <th>Fecha</th>
                    <th>Acciones</th>
                </tr>
                </thead>
                <tbody>
                {mociones.length > 0 ? (
                    mociones.map((mocion) => (
                        <tr key={mocion.ID}>
                            <td>{mocion.Topico}</td>
                            <td>{mocion.Tiempo_Del}</td>
                            <td>{mocion.Cant_Del}</td>
                            <td>{mocion.Tiempo_Total}</td>
                            <td>{mocion.Sesion}</td>
                            <td>{new Date(mocion.Fecha).toLocaleDateString()}</td>
                            <td>
                                <Link to={`/edit/${mocion.ID}`}>Editar</Link> |{' '}
                                <button onClick={() => handleDelete(mocion.ID)}>Eliminar</button> |{' '}
                                <button onClick={() => navigate(`/view/${mocion.ID}`)}>Ver</button>
                            </td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="7">No hay mociones disponibles</td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
};

export default MocionesList;
