import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getMocionById } from '../apiService';

function ViewMocion() {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const data = await getMocionById(id);
                setMocion(data);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    if (!mocion) return <div>Loading...</div>;

    return (
        <div>
            <h2>Delegados de la Moción: {mocion.Topico}</h2>
            {mocion.Delegado ? (
                <div>
                    <p>Nombre: {mocion.Delegado.Nombre}</p>
                    <p>Apellido: {mocion.Delegado.Apellido}</p>
                    <p>Delegación: {mocion.Delegado.Delegacion}</p>
                    {/* Otros detalles de los delegados */}
                </div>
            ) : (
                <p>No hay delegados para esta moción</p>
            )}
        </div>
    );
}

export default ViewMocion;
