import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const ViewMocion = () => {
    const { id } = useParams();
    const [mocion, setMocion] = useState(null);

    useEffect(() => {
        fetch(`http://localhost:8080/mociones/${id}`)
            .then((response) => response.json())
            .then((data) => setMocion(data))
            .catch((error) => console.error('Error fetching data:', error));
    }, [id]);

    if (!mocion) {
        return <div>Cargando...</div>;
    }

    return (
        <div>
            <h1>Detalles de la Moci贸n</h1>
            <p><strong>T贸pico:</strong> {mocion.Topico}</p>
            <p><strong>Tiempo por Delegado:</strong> {mocion.Tiempo_Del}</p>
            <p><strong>Cantidad de Delegados:</strong> {mocion.Cant_Del}</p>
            <p><strong>Tiempo Total:</strong> {mocion.Tiempo_Del * mocion.Cant_Del}</p>
            <p><strong>Sesi贸n:</strong> {mocion.Sesion}</p>
            <p><strong>Fecha:</strong> {mocion.Fecha}</p>
            <h2>Delegados:</h2>
            {mocion.Delegado ? (
                <div>
                    <p><strong>Nombre:</strong> {mocion.Delegado.Nombre}</p>
                    <p><strong>Apellido:</strong> {mocion.Delegado.Apellido}</p>
                    <p><strong>Delegaci贸n:</strong> {mocion.Delegado.Delegacion}</p>
                </div>
            ) : (
                <p>No hay delegados asociados.</p>
            )}
        </div>
    );
};

export default ViewMocion;
