import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';

const EditMocion = () => {
    const { id } = useParams();
    const history = useHistory();
    const [formData, setFormData] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: ''
    });

    useEffect(() => {
        fetch(`http://localhost:8080/mociones/${id}`)
            .then((response) => response.json())
            .then((data) => setFormData(data))
            .catch((error) => console.error('Error fetching data:', error));
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        formData.Tiempo_Total = formData.Tiempo_Del * formData.Cant_Del;
        fetch(`http://localhost:8080/mociones/${id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(formData)
        })
            .then(() => history.push('/'))
            .catch((error) => console.error('Error updating mocion:', error));
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Tópico:
                <input type="text" name="Topico" value={formData.Topico} onChange={handleChange} required />
            </label>
            <label>
                Tiempo por Delegado:
                <input type="number" name="Tiempo_Del" value={formData.Tiempo_Del} onChange={handleChange} required />
            </label>
            <label>
                Cantidad de Delegados:
                <input type="number" name="Cant_Del" value={formData.Cant_Del} onChange={handleChange} required />
            </label>
            <label>
                Sesión:
                <input type="text" name="Sesion" value={formData.Sesion} onChange={handleChange} required />
            </label>
            <label>
                Fecha:
                <input type="date" name="Fecha" value={formData.Fecha} onChange={handleChange} required />
            </label>
            <button type="submit">Guardar</button>
        </form>
    );
};

export default EditMocion;
