const handleCreate = () => {
    const newWindow = window.open('', '_blank', 'width=600,height=400');
    newWindow.document.write('<div id="root"></div>');
    newWindow.document.title = "Crear Moción";
    const addMocionScript = `<script type="text/babel">
    ReactDOM.render(<CreateMocion addMocion={${addMocion}} />, document.getElementById('root'));
  </script>`;
    newWindow.document.write(addMocionScript);
};
const handleEdit = (mocion) => {
    const newWindow = window.open('', '_blank', 'width=600,height=400');
    newWindow.document.write('<div id="root"></div>');
    newWindow.document.title = "Editar Moción";
    const editMocionScript = `<script type="text/babel">
    ReactDOM.render(<EditMocion mocion={${JSON.stringify(mocion)}} updateMocion={${updateMocion}} />, document.getElementById('root'));
  </script>`;
    newWindow.document.write(editMocionScript);
};
const handleView = (mocion) => {
    const newWindow = window.open('', '_blank', 'width=600,height=400');
    newWindow.document.write('<div id="root"></div>');
    newWindow.document.title = "Ver Moción";
    const viewMocionScript = `<script type="text/babel">
    ReactDOM.render(<ViewMocion mocion={${JSON.stringify(mocion)}} />, document.getElementById('root'));
  </script>`;
    newWindow.document.write(viewMocionScript);
};
