import React, { useState } from 'react';
import axios from 'axios';

const MocionForm = ({ onMocionAdded, onMocionUpdated }) => {
    const [formData, setFormData] = useState({
        ID: '',
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Tiempo_Total: '',
        Sesion: '',
        Fecha: ''
    });

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            if (formData.ID) {
                const response = await axios.put(`http://localhost:8080/mociones/${formData.ID}`, formData);
                onMocionUpdated(response.data);
            } else {
                const response = await axios.post('http://localhost:8080/mociones', formData);
                onMocionAdded(response.data);
            }
            setFormData({
                ID: '',
                Topico: '',
                Tiempo_Del: '',
                Cant_Del: '',
                Tiempo_Total: '',
                Sesion: '',
                Fecha: ''
            });
        } catch (error) {
            console.error('Error al enviar el formulario:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" name="Topico" value={formData.Topico} onChange={handleChange} placeholder="Tópico" />
            <input type="number" name="Tiempo_Del" value={formData.Tiempo_Del} onChange={handleChange} placeholder="Tiempo por Delegado" />
            <input type="number" name="Cant_Del" value={formData.Cant_Del} onChange={handleChange} placeholder="Cantidad de Delegados" />
            <input type="number" name="Tiempo_Total" value={formData.Tiempo_Total} onChange={handleChange} placeholder="Tiempo Total" />
            <input type="text" name="Sesion" value={formData.Sesion} onChange={handleChange} placeholder="Sesión" />
            <input type="date" name="Fecha" value={formData.Fecha} onChange={handleChange} />
            <button type="submit">{formData.ID ? 'Actualizar Moción' : 'Agregar Moción'}</button>
        </form>
    );
};

export default MocionForm;
