import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import apiService from '../apiService';

function EditMocion() {
    const { id } = useParams();
    const [form, setForm] = useState({
        Topico: '',
        Tiempo_Del: 0,
        Cant_Del: 0,
        Sesion: '',
        Fecha: ''
    });
    const navigate = useNavigate();

    useEffect(() => {
        async function fetchMocion() {
            try {
                const mocion = await apiService.getMocion(id);
                setForm(mocion);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        }
        fetchMocion();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setForm({
            ...form,
            [name]: value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const tiempoTotal = form.Tiempo_Del * form.Cant_Del;
        const updatedMocion = { ...form, Tiempo_Total: tiempoTotal };
        try {
            await apiService.updateMocion(id, updatedMocion);
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <label>
                Tópico:
                <input type="text" name="Topico" value={form.Topico} onChange={handleChange} />
            </label>
            <label>
                Tiempo por Delegado:
                <input type="number" name="Tiempo_Del" value={form.Tiempo_Del} onChange={handleChange} />
            </label>
            <label>
                Cantidad de Delegados:
                <input type="number" name="Cant_Del" value={form.Cant_Del} onChange={handleChange} />
            </label>
            <label>
                Sesión:
                <input type="text" name="Sesion" value={form.Sesion} onChange={handleChange} />
            </label>
            <label>
                Fecha:
                <input type="date" name="Fecha" value={form.Fecha} onChange={handleChange} />
            </label>
            <button type="submit">Guardar cambios</button>
        </form>
    );
}

export default EditMocion;
