import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditMocion = ({ onFetchMociones }) => {
    const { id } = useParams();
    const [mocion, setMocion] = useState({
        Topico: '',
        Tiempo_Del: '',
        Cant_Del: '',
        Sesion: '',
        Fecha: '',
    });
    const navigate = useNavigate();

    useEffect(() => {
        const fetchMocion = async () => {
            try {
                const response = await axios.get(`http://localhost:8081/mociones/${id}`);
                setMocion(response.data);
            } catch (error) {
                console.error('Error fetching mocion:', error);
            }
        };

        fetchMocion();
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        const tiempoTotal = parseInt(mocion.Tiempo_Del) * parseInt(mocion.Cant_Del);
        const updatedMocion = { ...mocion, Tiempo_Total: tiempoTotal };

        try {
            await axios.put(`http://localhost:8081/mociones/${id}`, updatedMocion);
            onFetchMociones();
            navigate('/');
        } catch (error) {
            console.error('Error updating mocion:', error);
        }
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setMocion({ ...mocion, [name]: value });
    };

    return (
        <div>
            <h2>Editar Moción</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Tópico:</label>
                    <input type="text" name="Topico" value={mocion.Topico} onChange={handleChange} />
                </div>
                <div>
                    <label>Tiempo por Delegado:</label>
                    <input type="number" name="Tiempo_Del" value={mocion.Tiempo_Del} onChange={handleChange} />
                </div>
                <div>
                    <label>Cantidad de Delegados:</label>
                    <input type="number" name="Cant_Del" value={mocion.Cant_Del} onChange={handleChange} />
                </div>
                <div>
                    <label>Sesión:</label>
                    <input type="text" name="Sesion" value={mocion.Sesion} onChange={handleChange} />
                </div>
                <div>
                    <label>Fecha:</label>
                    <input type="date" name="Fecha" value={mocion.Fecha} onChange={handleChange} />
                </div>
                <button type="submit">Guardar Cambios</button>
            </form>
        </div>
    );
};

export default EditMocion;
